    private void Crear() {
        String nombre = txtNombre.getText().trim();
        String descripcion = txtDescripcion.getText().trim();
        

        if (validarDatosPrograma (nombre, descripcion)) {
            crearPrograma(nombre, descripcion);
        }

    }

    private boolean validarDatosPrograma(String nombre, String descripcion) {
        if (nombre.isEmpty() ||  descripcion.isEmpty()) {
            JOptionPane.showMessageDialog(null, "No Deje Campos VacÃ­os.", "Error!", JOptionPane.ERROR_MESSAGE);
            return false;
        }
        if (descripcion.length() > 45) {
            JOptionPane.showMessageDialog(null, "La Descripcion Puede Tener Maximo 45 Caracteres", "Error", JOptionPane.ERROR_MESSAGE);
            return false;
        }
        

        return true;
    }

    private void crearPrograma(String nombre, String descripcion) {
        CursosGestor cursoGestor = new CursosGestor();
        Programas cursoNuevo = new Programas(nombre, descripcion);
        if (cursoGestor.crearCurso(cursoNuevo)) {
            JOptionPane.showMessageDialog(null, "Programa Creado con Exito", "Exito", JOptionPane.INFORMATION_MESSAGE);

        } else {
            JOptionPane.showMessageDialog(null, "El Programa Con El Nombre " + cursoNuevo.getNombre()+ " Ya Existe!", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }

    private void limpiarCampos() {
        txtNombre.setText(null);
        txtDescripcion.setText(null);
    }

